import { createWebHistory, createRouter } from "vue-router" 
import HomePage from "./App.vue"
import  Devices  from './components/MainDevices.vue';


const links = [
{
path: "/",     
name: "Home",     
component: HomePage,
},
{
path: "/devices",     
name: "Home",     
component: Devices,
},


]


const router = createRouter({   
history: createWebHistory(),  
 routes: links, 
})  

export default router
